// src/components/PortfolioHome.tsx
import React from "react";
import { Container, Header, Segment } from "semantic-ui-react";

export const PortfolioHome: React.FC = () => {
  return (
    <Container text textAlign="center" style={{ marginTop: "5em" }}>
      <Segment padded="very" style={{ border: "none", boxShadow: "none" }}>
        <Header as="h1" style={{ marginBottom: "1em" }}>
          Hello, I'm Adenekan Wonderful
        </Header>
        <p style={{ fontSize: "1.1rem" }}>
          I'm a passionate web developer, a teaching assistant at a coding
          bootcamp, and I also work in the construction industry. My diverse
          background allows me to bring a unique perspective to both web
          development and construction projects. In web development, I
          specialize in creating engaging and responsive web applications with a
          focus on clean, efficient code. As a TA, I have mentored and tracked
          the progress of over 50 students, helping them build strong
          foundations in programming and web development. Outside of coding and
          teaching, I apply my skills to construction projects, where attention
          to detail and problem-solving are key. When I'm not working, you can
          find me exploring new technologies, staying up-to-date with industry
          trends, and enjoying the process of continuous learning. Feel free to
          connect if you want to collaborate or learn more about my work.
        </p>
      </Segment>
    </Container>
  );
};
