import React, { useEffect, useState } from "react";
import "../style/About.css";

export const About = () => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setShow(true), 300); // Delay for animation
    return () => clearTimeout(timer);
  }, []);

  return (
    <div
      className={`About_about_EJ ${show ? "animate-in show" : "animate-in"}`}
    >
      <h2 className="About_about_heading">About Me</h2>
      <div className="About_about_container">
        <div className="About_about_container_card">
          <h3 className="About_about_Title">Past</h3>
          <p className="About_about_p">
            I discovered my passion for programming when I was just 14 years
            old...
          </p>
        </div>
        <div className="About_about_container_card">
          <h3 className="About_about_Title">Present</h3>
          <p className="About_about_p">
            Now, as a software engineering student at Springboard...
          </p>
        </div>
        <div className="About_about_container_card">
          <h3 className="About_about_Title">Future</h3>
          <p className="About_about_p">
            Looking ahead, I'm excited about the future of programming...
          </p>
        </div>
      </div>
    </div>
  );
};
