// src/components/PortfolioHome.tsx
import React from "react";
import { Container, Header, Segment } from "semantic-ui-react";
import styled from "styled-components";

// Styled component for custom styling
const CustomSegment = styled(Segment)`
  text-align: center;
  padding: 4em 2em;
`;

const CustomHeader = styled(Header)`
  color: #333;
  margin-bottom: 1em;
`;

const CustomParagraph = styled.p`
  font-size: 1.2em;
  color: #666;
  line-height: 1.6;
`;

export const PortfolioHome: React.FC = () => {
  return (
    <Container>
      <CustomSegment>
        <CustomHeader as="h1">Hello, I'm Ethan Jordon</CustomHeader>
        <CustomParagraph>
          Your friendly neighborhood frontend developer, UX architect, and
          JavaScript engineer. I spend my days (and often nights) painting the
          Internet canvas with projects and lines of code, turning zeroes and
          ones into immersive, interactive experiences. Bona fide photochromic
          lens enthusiastâ€”sunlight or indoors, I've got it covered. I tread the
          path of minimalism, finding beauty in simplicity and order. When I'm
          not crafting beautiful web experiences, you can find me reading
          articles or swaying to the rhythm of pop music & jazz, losing myself
          in the captivating flow of melodies. Anyway, you can contact me.
        </CustomParagraph>
      </CustomSegment>
    </Container>
  );
};
